import React, { useState } from "react";
import axios from "axios";
import { useAuth } from "../AuthContext";

const Upload = () => {
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [type, setType] = useState("image");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);
  const { token } = useAuth();

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!file || !title) {
      setError("Please provide both a title and a file.");
      return;
    }

    // Determine file type based on MIME type
    let fileType = "document";
    if (file.type.startsWith("image/")) {
      fileType = "image";
    } else if (file.type.startsWith("video/")) {
      fileType = "video";
    }

    // Override with user selection if different
    if (type !== fileType) {
      fileType = type;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("title", title);
    formData.append("type", fileType);

    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/media/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${token}`,
        },
      });
      setSuccess(`File uploaded successfully! Path: ${res.data.filePath}`);
      setFile(null);
      setTitle("");
      // Reset the file input
      document.getElementById("fileInput").value = "";
    } catch (err) {
      setError(err.response?.data?.error || "Upload failed. Please try again.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <main className="container my-5">
      <h1 className="text-center mb-4">Upload Media</h1>
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card shadow-sm">
            <div className="card-body">
              <form onSubmit={handleSubmit}>
                {error && <div className="alert alert-danger">{error}</div>}
                {success && <div className="alert alert-success">{success}</div>}

                <div className="mb-3">
                  <label htmlFor="title" className="form-label">Title</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    id="title" 
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="fileInput" className="form-label">File</label>
                  <input 
                    type="file" 
                    className="form-control" 
                    id="fileInput" 
                    onChange={handleFileChange}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="type" className="form-label">File Type</label>
                  <select 
                    className="form-select" 
                    id="type" 
                    value={type}
                    onChange={(e) => setType(e.target.value)}
                  >
                    <option value="image">Image</option>
                    <option value="video">Video</option>
                    <option value="document">Document</option>
                  </select>
                  <div className="form-text">Select the type of file you are uploading.</div>
                </div>

                <button 
                  type="submit" 
                  className="btn btn-primary w-100" 
                  disabled={loading}
                >
                  {loading ? "Uploading..." : "Upload File"}
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </main>
  );
};

export default Upload;
